// import logo from "./logo.svg";
import "./App.css";
import React from "react";
// import ReactDom from "reactdom";
import { Component } from "react";

class App extends Component {
  constructor(props) {
    super();

    this.state = {};
    this.newName = "John";
  }
  sayHeello(name) {
    return "Hello " + name;
  }

  render() {
    const myName = "Sammy";
    return (
      <div className="App">
        <h2>Just some sample data: {this.sayHeello(this.newName)}</h2>
        <h2>More sample data: {this.sayHeello(myName)}</h2>
        <Parent />
      </div>
    );
  }
}

class Parent extends Component {
  constructor(props) {
    super();

    this.state = {
      cars: ["s-BMW", "s-Audi", "s-Mercedes", "s-Ford"],
      r: 0,
    };

    this.handleClick = this.handleClick.bind(this);

    this.randomNumber = this.randomNumber.bind(this);
  }
  handleClick() {
    this.setState({ cars: this.props.cars });
  }

  render() {
    return (
      <div>
        <h2 onClick={this.handleClick}>Just Some Info</h2>
        <Cars
          mssg="cars are cool"
          modelNum="32456"
          coolCars={this.state.cars}
        />
      </div>
    );
  }
}

Parent.defaultProps = {
  cars: ["BMW", "Audi", "Mercedes", "Ford"],
};

class Cars extends Component {
  render() {
    return (
      <div>
        <h3>I am from cars component</h3>
        <p>{this.props.mssg}</p>
        <p>{this.props.modelNum}</p>
        <div>
          {this.props.coolCars.map((item, i) => {
            return <p key={i}>{item}</p>;
          })}
        </div>

        <button onClick={this.randomNumber}> Random Number</button>
        <Number myNumber={this.state.r} />
      </div>
    );
  }
}

class Number extends Component {
  render() {
    return <div>{this.props.myNumber}</div>;
  }
}

export default App;
